import React, { useState } from "react";
import { Link } from "react-router-dom";
import '../styles/SideBar.css'; // Importa tu archivo CSS con los estilos

const AnimatedText = () => {
  const [clicked, setClicked] = useState(false);

  const handleClick = () => {
    setClicked(!clicked);
  };

  return (
    <h1 className={`animated-text ${clicked ? 'clicked' : ''}`} onClick={handleClick}>
      {clicked ? "NQ" : "Mi Nombre"}
      <br />
      {clicked ? "Desarrollador Web" : "Subtítulo"}
    </h1>
  );
};

const SideBar = () => {
  return (
    <div className="sidebar">
      <AnimatedText />
      <ul>
        <li>
          <Link to="/about" className="sidebar-link">About →</Link>
        </li>
        <li>
          <a href="https://github.com/nicoquiroga90" className="sidebar-link">Git →</a>
        </li>
        <li>
          <Link to="/contact" className="sidebar-link">Contact →</Link>
        </li>
      </ul>
    </div>
  );
};

export default SideBar;
