import { useLocation } from 'react-router-dom';
import '../styles/About.css'

function Bio({ isHomePage }) {
  const location = useLocation();
  const isHome = location.pathname === '/';

  // El contenido del componente Bio depende de si estamos en la página de inicio o no
  if (isHomePage) {
    return (
      // Contenido específico para la página de inicio
      <p className="text-xl">
        Hola! I'm Gonzalo, a Senior Frontend developer in Copenhagen. I enjoy
        mentoring and delivering quality code for complex web applications,
        using modern tech and practices. Feel free to check out my coding skills
        on{' '}
        <a
          className="hover:text-primary"
          href="https://github.com/gonstoll"
          target="_blank"
          rel="noopener noreferrer"
        >
          my GitHub page
        </a>
        .
      </p>
    );
  } else {
    return (
      // Contenido para otras páginas que no sean la de inicio
      <p className="text-xl">
        Contenido para otras páginas que no sean la de inicio.
      </p>
    );
  }
}

export default Bio;
