import { useState } from 'react';
import { Link } from "react-router-dom";
import '../styles/SideBar.css'; // Importa tu archivo CSS con los estilos

const SideBar = () => {
  const [isNQ, setIsNQ] = useState(true); // Estado para controlar el texto

  // Función para alternar entre NQ y el nombre completo
  const toggleText = () => {
    setIsNQ(!isNQ);
  };

  // Definición del texto del nombre y subtitulo
  const name = isNQ ? "NQ" : "Nico Quiroga";
  const subtitle = isNQ ? "" : "Web Developer";

  return (
    <div className="sidebar">
      {/* Renderiza el nombre y el subtitulo */}
      <h1 onClick={toggleText}>{name}</h1>
      <h2>{subtitle}</h2>
      {/* Renderiza los enlaces */}
      <ul>
        <li>
          <Link to="/about" className="sidebar-link">About →</Link>
        </li>
        <li>
          <a href="https://github.com/nicoquiroga90" className="sidebar-link">Git →</a>
        </li>
        <li>
          <Link to="/contact" className="sidebar-link">Contact →</Link>
        </li>
      </ul>
    </div>
  );
};

export default SideBar;
