import React, { useState } from "react";
import '../styles/NameIcon.css'; // Importa tu archivo CSS con los estilos

const NameIcon = () => {
  const [clicked, setClicked] = useState(false);

  const toggleText = () => {
    setClicked(!clicked);
  };

  return (
    <h1
      className={`name-icon ${clicked ? 'clicked' : ''}`}
      onClick={toggleText}
      style={{ position: clicked ? 'fixed' : 'relative', top: clicked ? '20px' : 'auto', left: clicked ? '20px' : 'auto' }}
    >
      {clicked ? "NQ" : "Nico Quiroga"}
      <br />
      {clicked ? "" : "Desarrollador Web"}
    </h1>
  );
};

export default NameIcon;
