import { useState } from "react";
import '../styles/NameIcon.css'; 

const NameIcon = () => {
  const [clicked, setClicked] = useState(false);

  const toggleText = () => {
    setClicked(!clicked);
  };

  return (
    <>
    <h1 className={`name-icon ${clicked ? 'clicked' : ''}`} onClick={toggleText}>
      {clicked ? "Nico Quiroga" : "NQ"}
      <br /></h1>
     <h3> {clicked ? "Web Developer" : ""}</h3>
    </>
  );
};

export default NameIcon;
